INSERT INTO `product` VALUES (1,'https://auto365.vn/uploads/images/product/DVD/zestech/dx300.jpg',9.52,'ANDROID BOX ZESTECH DX300','Chiếc màn hình Zin trên ô tô của bạn quá “nghèo nàn” tính năng',1),
(2,'https://auto365.vn/uploads/images/product/DVD/zestech/dx265-pro.jpg',7.5,'ANDROID BOX ZESTECH DX265 PRO ','Thay đổi màn hình Zin sang màn hình Android đang là xu thế của thời đại. Tuy nhiên',1),
(3,'https://auto365.vn/uploads/images/product/DVD/zestech/dx265.jpg',7.5,'ANDROID BOX ZESTECH DX265','Android Box Zestech DX265 - xu thế chuyển đổi từ màn hình Zin sang màn hình Android hiện đại mà không cần thay đổi cấu trúc xe. Zestech DX265 hướng đến trải nghiệm của khách hàng khi sở hữu nhiều tính năng hữu dụng và hiện đại.',1),
(4,'https://auto365.vn/uploads/images/product/DVD/zestech/zestech-dx300-pro.jpg',9.5,'ANDROID BOX ZESTECH DX300 PRO','Android Box Zestech DX300 Pro là giải pháp biến đổi màn hình zin nguyên bản thành màn hình android thông minh để tăng trải nghiệm với những tính năng nổi trội cho người dùng xe hơi. Đặc biệt',1),
(5,'https://auto365.vn/uploads/images/product/DVD/zestech/ZT12.3-BASE-360.jpg',20,'MÀN HÌNH ANDROID Ô TÔ ZESTECH ZT12.3 BASE CAM360 ','Màn hình ô tô Zestech ZT12.3 Base Cam360 là dòng màn hình Android liền camera với những cải tiến vượt bậc. Màn hình ô tô Zestech ZT12.3 Base Cam360 giúp cho người lái mở rộng tầm nhìn',1),
(6,'https://auto365.vn/uploads/images/product/DVD/zestech/ZT22.jpg',16,'MÀN HÌNH ANDROID Ô TÔ ZESTECH ZT22','Màn hình android ô tô Zestech ZT12.3 Base là siêu phẩm đến từ thương hiệu Zestech được khách hàng săn đón nhiều bởi cấu hình khủng cùng trang bị thêm nhiều tính năng hiện đại',1),
(7,'https://auto365.vn/uploads/images/product/DVD/zestech/ZT360G.jpg',10.9,'MÀN HÌNH ANDROID Ô TÔ ZESTECH ZT360G','Màn hình android Zestech ZT360G là siêu phẩm với trang bị được nâng cấp hiện đại. Đây là dòng màn hình liền camera 360 độ tiếp tục giúp cho Zestech tạo nên sự bùng nổ trong phân khúc tầm trung. Sản phẩm cung cấp đầy đủ các chức năng cho người dùng',1),
(8,'https://auto365.vn/uploads/images/product/DVD/zestech/ZT360.jpg',17.5,'MÀN HÌNH ANDROID Ô TÔ ZESTECH ZT360 ','Màn hình Android ô tô Zestech ZT360 là một bước tiến đột phá lớn của thương hiệu nổi tiếng Zestech. Khi vừa được ra mắt thì Zestech ZT360 đã nhận được sự ủng hộ nhiệt tình của hầu hết các tài xế bởi nhờ ưu thế thông minh',1),
(9,'https://auto365.vn/uploads/images/product/DVD/zestech/S100J.jpg',6.9,'MÀN HÌNH ANDROID Ô TÔ ZESTECH S100J','Màn hình Android ô tô Zestech ZT360 là một bước tiến đột phá lớn của thương hiệu nổi tiếng Zestech. Khi vừa được ra mắt thì Zestech ZT360 đã nhận được sự ủng hộ nhiệt tình của hầu hết các tài xế bởi nhờ ưu thế thông minh',1),
(10,'https://auto365.vn/uploads/images/product/DVD/zestech/S500.jpg',10.8,'MÀN HÌNH ANDROID Ô TÔ ZESTECH S500','Zestech Z500 là màn hình android thông đến từ thương hiệu nổi tiếng Zestech. Những năm gần đây màn hình Zestech ngày càng trở nên phổ biến hơn và dành được sự quan tâm nhất định của khách hàng Việt',1),
(11,'https://auto365.vn/uploads/images/product/DVD/zestech/S500.jpg',8.8,'MÀN HÌNH ANDROID Ô TÔ ZESTECH Z500','Với thiết kế thông minh để hỗ trợ tài xế một cách tối ưu',1),(12,'https://auto365.vn/uploads/images/product/DVD/zestech/Z500-NEW.jpg',11,'MÀN HÌNH ANDROID Ô TÔ ZESTECH Z500 NEW','Màn hình android ô tô Zestech Z500 new là phiên bản nâng cấp đến từ thương hiệu Zestech. Màn hình android Zestech Z500 New với hiệu năng vô cùng mạnh mẽ',1),
(13,'https://auto365.vn/uploads/images/product/DVD/zestech/Z800-PRO-PLUS-LIMITED.jpg',30,'MÀN HÌNH ANDROID Ô TÔ ZESTECH Z800 PRO+ LIMITED','Màn hình Zestech Z800 PRO+ LIMITED là sản phẩm giới hạn trên toàn cầu được đánh giá khá tốt trong thị trường.',1),
(14,'https://auto365.vn/uploads/images/product/DVD/zestech/Z900.jpg',19.5,'MÀN HÌNH ANDROID Ô TÔ ZESTECH Z900','Zestech là thương hiệu màn hình android được đánh giá cao tại thị trường Việt Nam với các sản phẩm chính hãng chất lượng cao. Trong đó màn hình Zestech được người tiêu dùng đánh giá là có cấu hình khủng với hiệu suất cao nhất.',1),
(15,'https://auto365.vn/uploads/images/product/DVD/zestech/Z800-PRO-PLUS.jpg',24,'MÀN HÌNH ANDROID Ô TÔ ZESTECH Z800 PRO+','Màn hình Android Z800 Pro+ là một bước tiến đột phá vượt bậc của Zestech',1),
(16,'https://auto365.vn/uploads/images/product/DVD/zestech/Z800-PLUS-PRO.jpg',23,'MÀN HÌNH ANDROID Ô TÔ ZESTECH Z800+ NÂNG CẤP','Cùng khám phá xem những tính năng thông minh được tích hợp trong màn hình Android ô tô Zestech Z800+ nâng cấp này nhé',1);